# Bases de donn√©es

CREATE DATABASE ma_base;
USE ma_base;
DROP DATABASE ma_base;

# SELECT

Lire des donn√©es.

SELECT * FROM users;
SELECT nom, email FROM users WHERE age > 18;
SELECT * FROM users ORDER BY nom ASC;

# UPDATE

Modifier des donn√©es.

UPDATE users SET email = 'new@mail.com' WHERE id = 1;

# DELETE

Supprimer des donn√©es.

DELETE FROM users WHERE id = 5;
TRUNCATE TABLE users;

# Index

Acc√©l√©rer les requ√™tes.

CREATE INDEX idx_email ON users(email);
SHOW INDEX FROM users;

# Transactions

ATOMICIT√â garantie.

START TRANSACTION;
UPDATE comptes SET solde = solde - 100 WHERE id = 1;
UPDATE comptes SET solde = solde + 100 WHERE id = 2;
COMMIT;

# Arrow Functions

Syntaxe moderne.

// Ancien
function add(a, b) {
  return a + b;
}

// Moderne
const add = (a, b) => a + b;

# Destructuring

Extraire valeurs facilement.

const {nom, age} = user;
const [first, ...rest] = array;

# Spread

copier/fusionner.

const arr2 = [...arr1];
const obj2 = {...obj1, new: 'value'};

# Async/Await

Promises plus lisibles.

async function getData() {
  try {
    const res = await fetch(url);
    const data = await res.json();
    return data;
  } catch (err) {
    console.error(err);
  }
}

# Modules

Organiser le code.

// export
export const PI = 3.14;
export function add(a,b) {}

// import
import {PI, add} from './math.js';

# Classes

POO en JavaScript.

class User {
  constructor(nom) {
    this.nom = nom;
  }

  sayHello() {
    return `Hello ${this.nom}`;
  }
}

# Optional Chaining

√âviter erreurs null.

const name = user?.profile?.name;
const result = obj?.method?.();

# Nullish ??

Valeur par d√©faut.

const value = input ?? 'default';
// Seulement si null/undefined

# Symbols

Identifiants uniques.

const sym = Symbol('desc');
const obj = {[sym]: 'value'};

# Proxy

Intercepter op√©rations.

const proxy = new Proxy(target, {
  get(obj, prop) {
    console.log(`Get ${prop}`);
    return obj[prop];
  }
});

# JSX

JavaScript XML.

const element = <h1>Hello!</h1>;

const user = {
  name: 'Alice'
};
const greeting = <h1>Hello {user.name}</h1>;

# Composants

Briques de base.

// Function Component
function Welcome(props) {
  return <h1>Hello {props.name}</h1>;
}

// Arrow function
const Welcome = ({name}) => <h1>Hello {name}</h1>;

# Props

Passer des donn√©es.

<UserCard name="Alice" age={25} />

function UserCard({name, age}) {
  return <div>{name}: {age} ans</div>;
}

# useState Hook

√âtat local.

import {useState} from 'react';

function Counter() {
  const [count, setCount] = useState(0);

  return (
    <button onClick={() => setCount(count + 1)}>
      Count: {count}
    </button>
  );
}

# useEffect

Effets de bord.

useEffect(() => {
  // Code √† ex√©cuter
  console.log('Mounted');

  return () => {
    // Cleanup
    console.log('Unmounted');
  };
}, [dependencies]);

# Events

G√©rer interactions.

function Button() {
  const handleClick = (e) => {
    e.preventDefault();
    console.log('Clicked!');
  };

  return <button onClick={handleClick}>Click</button>;
}

# Forms

Contr√¥ler les inputs.

const [value, setValue] = useState('');

<input 
  value={value}
  onChange={(e) => setValue(e.target.value)}
/>

# Lists

Afficher tableaux.

const items = ['a', 'b', 'c'];

return (
  <ul>
    {items.map((item, i) => (
      <li key={i}>{item}</li>
    ))}
  </ul>
);

# Conditional

{isLoggedIn ? <Dashboard /> : <Login />}

{error && <ErrorMessage />}

{loading ? <Spinner /> : <Content />}

# Context API

Partager donn√©es globalement.

const ThemeContext = React.createContext();

function App() {
  return (
    <ThemeContext.Provider value="dark">
      <Child />
    </ThemeContext.Provider>
  );
}

# useRef

R√©f√©rences DOM.

const inputRef = useRef();

useEffect(() => {
  inputRef.current.focus();
}, []);

<input ref={inputRef} />

# Custom Hooks

R√©utiliser logique.

function useLocalStorage(key) {
  const [value, setValue] = useState(
    () => localStorage.getItem(key)
  );

  useEffect(() => {
    localStorage.setItem(key, value);
  }, [key, value]);

  return [value, setValue];
}

# if/elif/else

if age >= 18:
    print("Majeur")
elif age >= 13:
    print("Ado")
else:
    print("Enfant")

# Loops

# for
for i in range(5):
    print(i)

for item in liste:
    print(item)

# while
while x < 10:
    x += 1

# Classes

class User:
    def __init__(self, nom):
        self.nom = nom

    def saluer(self):
        return f"Bonjour {self.nom}"

user = User("Alice")
print(user.saluer())

# H√©ritage

class Animal:
    def speak(self):
        pass

class Dog(Animal):
    def speak(self):
        return "Woof!"

# Decorators

def timer(func):
    def wrapper(*args):
        import time
        start = time.time()
        result = func(*args)
        print(f"Temps: {time.time()-start}s")
        return result
    return wrapper

@timer
def slow():
    time.sleep(1)

# Basics

// server.js
const http = require('http');

const server = http.createServer((req, res) => {
  res.writeHead(200, {'Content-Type': 'text/plain'});
  res.end('Hello Node!');
});

server.listen(3000);

# NPM

npm init -y
npm install express
npm install --save-dev nodemon

// package.json
"scripts": {
  "start": "node server.js",
  "dev": "nodemon server.js"
}

# Express

Framework web minimaliste.

const express = require('express');
const app = express();

app.get('/', (req, res) => {
  res.send('Hello Express!');
});

app.listen(3000);

# Routing

// Routes
app.get('/users', getUsers);
app.post('/users', createUser);
app.put('/users/:id', updateUser);
app.delete('/users/:id', deleteUser);

// Params
app.get('/users/:id', (req, res) => {
  const id = req.params.id;
});

# Middleware

Fonctions interm√©diaires.

// Logger
app.use((req, res, next) => {
  console.log(`${req.method} ${req.url}`);
  next();
});

// Body parser
app.use(express.json());
app.use(express.urlencoded({extended: true}));

# MongoDB

npm install mongodb mongoose

const mongoose = require('mongoose');

mongoose.connect('mongodb://localhost/mydb', {
  useNewUrlParser: true
});

# Models

const userSchema = new mongoose.Schema({
  nom: String,
  email: {type: String, unique: true},
  age: Number,
  createdAt: {type: Date, default: Date.now}
});

const User = mongoose.model('User', userSchema);

# CRUD

// Create
const user = new User({nom: "Alice"});
await user.save();

// Read
const users = await User.find();
const user = await User.findById(id);

// Update
await User.findByIdAndUpdate(id, {nom: "Bob"});

// Delete
await User.findByIdAndDelete(id);

# JWT Auth

npm install jsonwebtoken bcryptjs

const jwt = require('jsonwebtoken');

// Cr√©er token
const token = jwt.sign({userId}, 'secret', {expiresIn: '1d'});

// V√©rifier
const decoded = jwt.verify(token, 'secret');

# Validation

npm install joi

const Joi = require('joi');

const schema = Joi.object({
  nom: Joi.string().min(3).required(),
  email: Joi.string().email().required()
});

const {error} = schema.validate(data);

# Errors

// Error middleware
app.use((err, req, res, next) => {
  console.error(err.stack);
  res.status(500).json({error: err.message});
});

// Async errors
const asyncHandler = fn => (req, res, next) =>
  Promise.resolve(fn(req, res, next)).catch(next);

